mapper.select:
SELECT u.id, u.username, u.SDUId, u.major, g.regular, g.grade, g.final_score, sec.number, sec.major AS sectionNumber FROM cms.user u JOIN cms.status s ON u.id = s.id JOIN cms.section sec ON s.section = sec.id JOIN cms.grade g ON u.id = g.student_id WHERE u.id IN
#{id}
AND g.course_id = #{courseId}

SELECT * FROM classes

    
        AND name LIKE CONCAT('%', #{keyword}, '%')
    
    
        AND term = #{term}
    
    
        AND type = #{type}
    
        AND status = 1

ORDER BY id DESC

SELECT id, class_num, name, point, term, status, regular_ratio, final_ratio, type  FROM classes

    
        AND (
        name LIKE CONCAT('%', #{keyword}, '%')
        OR category LIKE CONCAT('%', #{keyword}, '%')
        )
    
    
        AND term = #{term}
    
    AND teacher_id = #{id}

ORDER BY id DESC
LIMIT #{offset}, #{pageSize}
