mapper.select:
SELECT * FROM classes

    
        AND name LIKE CONCAT('%', #{keyword}, '%')
    
    
        AND term = #{term}
    
    
        AND type = #{type}
    
        AND status = 1

ORDER BY id DESC

SELECT id, class_num, name, point, term, status, regular_ratio, final_ratio, type  FROM classes

    
        AND (
        name LIKE CONCAT('%', #{keyword}, '%')
        OR category LIKE CONCAT('%', #{keyword}, '%')
        )
    
    
        AND term = #{term}
    
    AND status = 1
    AND teacher_id = #{id}

ORDER BY id DESC
LIMIT #{offset}, #{pageSize}
