SELECT u.id, u.SDUId AS SDUId, u.username, u.sex, u.major, s.grade, s.section, s.status
FROM user u
LEFT JOIN status s ON u.id = s.id
WHERE u.permission = 2

    AND u.major = #{major}


    AND s.grade = #{grade}


    AND s.status = #{status}

LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM user u
LEFT JOIN status s ON u.id = s.id
WHERE u.permission = 2

    AND u.major = #{major}


    AND s.grade = #{grade}


    AND s.status = #{status}

